mapper.select:
	select 

  from 
  WSC_DOCUMENT doc 
  where 
  apply_user_id = #{userId,jdbcType=VARCHAR}

  	and operate_user = #{doctorName,jdbcType=VARCHAR}


  	and org_id = #{orgId,jdbcType=VARCHAR}



  		and operate_start_time >= #{startDate,jdbcType=VARCHAR}




  		and operate_start_time <= #{endDate,jdbcType=VARCHAR}

	select 

  from 
  WSC_DOCUMENT doc 
  where 
  1=1 

  	and org_id = #{orgId,jdbcType=VARCHAR}


  	and patient_name = #{patientName,jdbcType=VARCHAR}



  		and operate_start_time >= #{startDate,jdbcType=VARCHAR}




  		and operate_start_time <= #{endDate,jdbcType=VARCHAR}


  order by operate_start_time desc

select * from (
select doc.document_id, doc.operative_id, (select GROUP_CONCAT(operative_name) from WSC_OPERATIVE where operative_id in ( ( select substring_index(substring_index(doc.operative_id, ',' , help_topic_id + 1), ',' , -1) from mysql.help_topic where help_topic_id < (length(doc.operative_id) - length(REPLACE(doc.operative_id, ',', '')) + 1 ) ) )) as operative_name, doc.anesthetic_id, (select anesthetic_name from WSC_ANESTHETIC where anesthetic_id = doc.anesthetic_id) as anesthetic_name, doc.patient_name, doc.patient_age, doc.patient_sex,
doctmp.operative_id as tmp_operative_id, (select GROUP_CONCAT(operative_name) from WSC_OPERATIVE where operative_id in ( ( select substring_index(substring_index(doctmp.operative_id, ',' , help_topic_id + 1), ',' , -1) from mysql.help_topic where help_topic_id < (length(doctmp.operative_id) - length(REPLACE(doctmp.operative_id, ',', '')) + 1 ) ) )) as tmp_operative_name, doctmp.anesthetic_id as tmp_anesthetic_id, (select anesthetic_name from WSC_ANESTHETIC where anesthetic_id = doctmp.anesthetic_id) as tmp_anesthetic_name, doctmp.patient_name as tmp_patient_name, doctmp.patient_age as tmp_patient_age, doctmp.patient_sex as tmp_patient_sex,
doc.patient_num, doc.patient_bednum, doc.patienttype_id, doc.operationtype_id, doc.document_title, doc.document_type, doc.operate_user, doc.operate_aide, doc.apply_user_id, doc.admin_user_id, doc.qa_user_id, (select user_name from WSC_USER where user_id = doc.qa_user_id) as qa_user_name, doc.qa_team_id, doc.hospital_memo, doc.qa_memo, doc.admin_memo, doc.memo, doc.document_state, doc.operate_start_time, doc.operate_end_time, DATE_FORMAT(doc.create_date, '%Y-%m-%d %H:%k:%s') as create_date, doc.hospital_evaluate, doc.hospital_evaluate_memo, doc.doctor_evaluate, doc.doctor_evaluate_memo, doc.old_document_state, doc.delete_reason, doc.transfer_user_id,
doc.org_id, org.org_name, org.org_address, org.org_tel, org.leader_name, org.leader_tel, org.province as org_province, org.city as org_city, org.area as org_area,
doctmp.height, doctmp.weight, doctmp.asa, doctmp.xtbs, doctmp.shs, doctmp.shcs, doctmp.sskssj, doctmp.ssjssj, doctmp.sssc, doctmp.jmjs, doctmp.ywsj_hxxt, doctmp.ywsj_hxxt_qt, doctmp.ywsj_xhxt, doctmp.ywsj_xhxt_qt, doctmp.ywsj_gm, doctmp.ywsj_gm_socre, doctmp.ywsj_qt, doctmp.shbl_zw, doctmp.shbl_hbtt, doctmp.shbl_ex, doctmp.shbl_xy, doctmp.shbl_ot, doctmp.shbl_ot_score, doctmp.shttzl, doctmp.photo_1, doctmp.photo_2, doctmp.photo_3, doctmp.photo_4, doctmp.photo_5, doctmp.status from WSC_DOCUMENT doc left join WSC_DOCUMENT_TMP doctmp on doc.document_id = doctmp.document_id left join WSC_ORGANIZATION org on doc.org_id = org.org_id where 1=1
and ( org.org_id in ( select org_id from WSC_USER t_u left join WSC_ORGANIZATION t_o on t_u.role_type_id = t_o.org_pid where t_u.user_id = #{applyUserId,jdbcType=VARCHAR} ) or org.org_id in ( select role_type_id from WSC_USER where user_id = #{applyUserId,jdbcType=VARCHAR} ) )
and ( doc.qa_user_id = #{qaUserId,jdbcType=VARCHAR} or doc.document_id in ( select doc.document_id from WSC_QATEAM wq where wq.user_id = #{qaUserId,jdbcType=VARCHAR} and wq.team_id = doc.qa_team_id ) )
and org.province = #{province,jdbcType=VARCHAR}
and org.city = #{city,jdbcType=VARCHAR}
and org.area = #{area,jdbcType=VARCHAR}
and doc.qa_user_id = #{qaUserId,jdbcType=VARCHAR}
and doc.document_state in (${documentState})
and doc.document_id = #{documentId,jdbcType=VARCHAR}
and doc.operate_start_time >= #{operateStartTime,jdbcType=VARCHAR}
and doc.operate_start_time <= #{operateEndTime,jdbcType=VARCHAR}
and doc.patient_name like concat(#{patientName,jdbcType=VARCHAR}, '%')
) tmpTab where 1=1
and qa_user_name like concat(#{qaUserName,jdbcType=VARCHAR}, '%')
order by operate_start_time desc


    	select wu.user_id,
    		   wu.user_name,
    		   (select DISTINCT(1) 
    		   	from WSC_RECORD wr
    		   	where wr.user_id = wu.user_id 
    		   	and date_format(wr.record_date, '%Y-%m-%d %h:%i:%s') <= #{operate_start_time,jdbcType=VARCHAR} 
    		   	and date_format(wr.end_date, '%Y-%m-%d %h:%i:%s') >= #{operate_start_time,jdbcType=VARCHAR} 
   		   		and org_id = #{org_id,jdbcType=VARCHAR}
    			) as isrecord,
    		   (
				select DISTINCT(1) 
				from WSC_CALENDAR wc 
				where wc.user_id = wu.user_id 
				and date_format(wc.start_time, '%Y-%m-%d %h:%i:%s') <= #{operate_start_time,jdbcType=VARCHAR} 
				and date_format(wc.end_time, '%Y-%m-%d %h:%i:%s') >= #{operate_start_time,jdbcType=VARCHAR} 
			   ) as iscalendar,
    		   wu.province,
    		   wu.city,
    		   wu.area,
    		   wu.patient_type,
    		   wu.operation_type
    	from WSC_USER wu
    	where role_id = '5'
    	and province = #{province,jdbcType=VARCHAR} 



    		and wu.user_name like concat(#{qaName,jdbcType=VARCHAR}, '%')


	select user_id,
		   user_name,
		   sex,
		   age
	from WSC_USER
	where 1=1 
	and user_id <> #{NoUserId,jdbcType=VARCHAR}
	and province = #{province,jdbcType=VARCHAR}
	and role_id = #{roleId,jdbcType=VARCHAR}


  		and user_name = #{userName,jdbcType=VARCHAR}
