mapper.select:
select distinct
,
from WSC_CALENDAR cal inner join WSC_USER user on user.user_id=cal.user_id left join WSC_RECORD rd on cal.user_id=rd.user_id where user.role_id='5' and user.city like concat(#{cityPre,jdbcType=VARCHAR},'%') and cal.end_time > now()
and user.user_name = #{doctorName,jdbcType=VARCHAR}
and rd.org_name = #{orgName,jdbcType=VARCHAR}
and end_time >= str_to_date( #{startDate},'%Y-%m-%d %H:%i:%s')
and cal.start_time <= str_to_date( #{endDate},'%Y-%m-%d %H:%i:%s')

mapper.insert:
insert into WSC_CALENDAR (calendar_id, user_id, title, 
  start_time, end_time, memo
  )
values (#{calendarId,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, 
  #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{memo,jdbcType=VARCHAR}
  )
